mapper.insert:
INSERT INTO KSSA_NOTICE
(
	TITLE
	, CONTENTS
	, USER_NAME
	, INSERT_ID
	, INSERT_DATE

)
VALUES(
	#{title}
	,#{contents}
	,#{userName}
	,#{insertId}
	,now()
)

mapper.update:

UPDATE KSSA_NOTICE
SET 
	TITLE = #{title}
	, CONTENTS = #{contents}
	, USER_NAME = #{userName}
	, UPDATE_ID = #{updateId}
	, UPDATE_DATE = now()
	, USE_YN = #{useYn}
WHERE SEQ_ID = #{seqId}

